import React from 'react';
import $ from 'jquery';

import { BusAPI } from 'lib/bus';

interface SettingsStates {
  busStation: JSX.Element[]
}

class Settings extends React.Component<{}, SettingsStates> {
  constructor(props: any) {
    super(props);

    this.setBusStation = this.setBusStation.bind(this)
  }

  state = {
    busStation: []
  }

  searchBusStation() {
    $('#search-bus-station').keyup((event) => {
      const name = $(event.target).val()!.toString();

      const api = new BusAPI();
      api.getBusStation(name).then(data => {
        data = data ? data.slice(0, 5) : undefined;

        const resultTarget = $('#search-bus-station-result');
        resultTarget.html('');
        for(const i in data) {  
          resultTarget.append(
            `<li data-ars-id=${data[i].arsId}>${data[i].stNm}</li>`
          );
        }
      });
    });
  }

  setBusRoute() {
    $('#search-bus-station-result > li').click((event) => {
      const arsId = $(event.target).data('ars-id');

      const api = new BusAPI();
      api.getBusRoute(arsId).then(data => {
        console.log(data);
      });
    });
  }

  componentDidMount() {
  }

  setBusStation(event: any) {
    const name = event.target.value;

    const api = new BusAPI();
    api.getBusStation(name).then(data => {
      data = data ? data.slice(0, 5) : undefined;

      const arr = [];
      for(const i in data) {  
        arr.push(
          <li data-ars-id={data[i].arsId}>{data[i].stNm}</li>
        );
      }
      this.setState({'busStation': arr});
    });
  }

  render() {
    return (
      <div className="list">
        <div>
          <input type="text" id="search-bus-station" onChange={this.setBusStation}/>
          <ul id="search-bus-station-result">
            {this.state.busStation}
          </ul>
          <table id="bus-route-result">
            <tbody>
            </tbody>
          </table>
        </div>
        <div>
        </div>
      </div>
    );
  }
}

export default Settings;
