import { types } from 'mobx-state-tree';
let store = null;
const Store = types.model({
  token: types.string,
  isSignin: false
}).actions(self => {
  const signin = token => {
    self.isSignin = true, self.token = token;
  };

  const signout = () => {
    self.isSignin = false, self.token = '';
  };

  return {
    signin,
    signout
  };
});
export const initializeStore = (snapshot = null) => {
  if (store === null) {
    store = Store.create({
      isSignin: false,
      token: ''
    });
  }

  if (snapshot) {
    store = Store.create({
      isSignin: false,
      token: ''
    });
  }

  return store;
};
