class TimeUI {
  setWaitingTime(target, sec, callback) {
    const interval = new Interval();
    const id = interval.set(() => {
      const minute = sec / 60 >> 0;
      const minute_message = minute ? String(minute) + 'ë¶„' : '';
      const sec_message = String(sec - minute * 60) + 'ì´ˆ';
      target.text(minute_message + sec_message);
