const SIGNIN = 'signin/SIGNIN';
const SIGNOUT = 'signin/SIGNOUT';

export const signinAction = {
  signin: (token: string) => ({ type: SIGNIN, isSignin: true, token: token }),
  signout: () => ({ type: SIGNOUT, isSignin: false, token: '' }),
};

type SigninAction = ReturnType<typeof signinAction.signin>;
type SignoutAction = ReturnType<typeof signinAction.signout>;
type Actions = SigninAction | SignoutAction;

export type SigninState = Readonly<{
  isSignin: boolean,
  token: string
}>;

const initialState: SigninState = {
  isSignin: false,
  token: '',
};

const reducer = (state: SigninState = initialState, action: Actions): SigninState => {
  switch(action.type) {
    case SIGNIN:
      return {
        ...state,
        isSignin: (action as SigninAction).isSignin,
        token: (action as SigninAction).token,
      };
    case SIGNOUT:
      return {
        ...state,
        isSignin: (action as SignoutAction).isSignin,
        token: (action as SignoutAction).token,
      };
    default: 
      return state;
  }
}

export default reducer;
