import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import Header from 'components/layout/header';
import Title from 'components/pages/title';
import Logo from 'components/pages/logo';
import TextOnlyTab from 'components/pages/textOnlyTab';
import BlankList from 'components/pages/blankList';
import SNSLogin from 'components/pages/snsLogin';
import Footer from 'components/pages/footer';
import Redirect from 'components/layout/redirect';

import { withRedux } from 'store/redux';

interface SigninPageProps {
  token: string
}


class SigninPage extends React.Component<SigninPageProps> {
  isSignin: any = useSelector((state: any) => state.isSignin)

  static getInitialProps(ctx: any) {
    return {token: ctx.query.token}
  }

  signin() {
    const dispatch = useDispatch();
    dispatch({type: 'SIGNIN', token: this.props.token});
  }

  render() {
    if(this.props.token != undefined) {
      this.signin();
    }
    if(this.isSignin == true) {
      return <Redirect to='/'/>
    }

    return (
      <div>
        <Header title='회원가입'/>

        <Title/>
        <Logo/>
        <TextOnlyTab title='회원가입'/>
        <BlankList>
          <SNSLogin/>
        </BlankList>

        <Footer/>
      </div>
    );
  }
}

export default withRedux(SigninPage);
