import React from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';

import Header from 'components/layout/header';
import Title from 'components/pages/title';
import Logo from 'components/pages/logo';
import TextOnlyTab from 'components/pages/textOnlyTab';
import BlankList from 'components/pages/blankList';
import SNSLogin from 'components/pages/snsLogin';
import Footer from 'components/pages/footer';
import Redirect from 'components/layout/redirect';

import { State } from 'store/actions';
import { signinAction } from 'store/actions/signin';

interface SigninPageProps {
  accessToken: string,
  SigninAction: typeof signinAction
}

class SigninPage extends React.Component<SigninPageProps> {
  static getInitialProps(ctx: any) {
    return {accessToken: ctx.query.token}
  }

  render() {
    const token = this.props.accessToken;
    console.log(this.props);
    if(token) {
      {this.props.SigninAction.signin(token)}
      return <Redirect to='/'/>
    }

    return (
      <div>
        <Header title='EASYWAY'/>

        <Title/>
        <Logo/>
        <TextOnlyTab title='회원가입'/>
        <BlankList>
          <SNSLogin/>
        </BlankList>

        <Footer/>
      </div>
    );
  }
}

export default connect(
  ({ signin }: State) => ({
    isSignin: signin.isSignin,
    token: signin.token
  }),
  (dispatch) => ({
    SigninAction: bindActionCreators(signinAction, dispatch),
  })
)(SigninPage);
