import React from 'react';
import Router from 'next/router';

interface RedirectProps {
  to: string
  local: {[key: string]: any}
}

class Redirect extends React.Component<RedirectProps> {
  componentDidMount() {
    for(const key in this.props.local) {
      localStorage.setItem(key, this.props.local[key])
    }
    if(localStorage.getItem('isSignin') == 'true') {
      Router.push(this.props.to)
    }
  }

  render() {
    return (
      <div>redirect...</div>
    );
  }
}

export default Redirect;
